    @apply_utils Antiderivative((~(a') + ~(b') * atanh(~(c') * ~x)) ^ ~p / ~x, ~x) => 2 * (~a + ~b * atanh(~c * ~x)) ^ ~p * atanh(1 - 2 / (1 - ~c * ~x)) - 2 * ~b * ~c * ~p * Antiderivative(((~a + ~b * atanh(~c * ~x)) ^ (~p - 1) * atanh(1 - 2 / (1 - ~c * ~x))) / (1 - (~c) ^ 2 * (~x) ^ 2), ~x) <-- FreeQ([~a, ~b, ~c], ~x) && IGtQ(~p, 1)
