    @apply_utils Antiderivative((~x) ^ ~(m') * (~(a') + ~(b') * atanh(~(c') * (~x) ^ ~(n'))) ^ ~(p'), ~x) => ((~x) ^ (~m + 1) * (~a + ~b * atanh(~c * (~x) ^ ~n)) ^ ~p) / (~m + 1) - ((~b * ~c * ~n * ~p) / (~m + 1)) * Antiderivative(((~x) ^ (~m + ~n) * (~a + ~b * atanh(~c * (~x) ^ ~n)) ^ (~p - 1)) / (1 - (~c) ^ 2 * (~x) ^ (2 * ~n)), ~x) <-- FreeQ([~a, ~b, ~c, ~m, ~n], ~x) && (IGtQ(~p, 0) && ((EqQ(~p, 1) || EqQ(~n, 1) && IntegerQ(~m)) && NeQ(~m, -1)))
