@testset "1.2.3.3 (d+e x^n)^q (a+b x^n+c x^(2 n))^p" begin
    (a, b, c, d, e, f, n, p, q, x, ) = @variables a b c d e f n p q x

    #= ::Package:: =#

    #= ::Title::Closed:: =#
    #=Integrands*of*the*form*(d+e*x^3)^q*(a+b*x^3+c*x^6)^p=#


    #= ::Section::Closed:: =#
    #=Integrands*of*the*form*(d+e*x^3)^q*(a+b*x^3+c*x^6)^p*with*b=0=#


    @test_int [(d + e*x^3)/(a + c*x^6), x, 12, (d*atan((c^(1/6)*x)/a^(1/6)))/(3*a^(5/6)*c^(1/6)) - ((sqrt(c)*d + sqrt(3)*sqrt(a)*e)*atan(sqrt(3) - (2*c^(1/6)*x)/a^(1/6)))/(6*a^(5/6)*c^(2/3)) + ((sqrt(c)*d - sqrt(3)*sqrt(a)*e)*atan(sqrt(3) + (2*c^(1/6)*x)/a^(1/6)))/(6*a^(5/6)*c^(2/3)) - (e*log(a^(1/3) + c^(1/3)*x^2))/(6*a^(1/3)*c^(2/3)) - ((sqrt(3)*sqrt(c)*d - sqrt(a)*e)*log(a^(1/3) - sqrt(3)*a^(1/6)*c^(1/6)*x + c^(1/3)*x^2))/(12*a^(5/6)*c^(2/3)) + ((sqrt(3)*sqrt(c)*d + sqrt(a)*e)*log(a^(1/3) + sqrt(3)*a^(1/6)*c^(1/6)*x + c^(1/3)*x^2))/(12*a^(5/6)*c^(2/3))]
    @test_int [(d + e*x^3)/(a - c*x^6), x, 13, -(((d - (sqrt(a)*e)/sqrt(c))*atan((a^(1/6) - 2*c^(1/6)*x)/(sqrt(3)*a^(1/6))))/(2*sqrt(3)*a^(5/6)*c^(1/6))) + ((sqrt(c)*d + sqrt(a)*e)*atan((a^(1/6) + 2*c^(1/6)*x)/(sqrt(3)*a^(1/6))))/(2*sqrt(3)*a^(5/6)*c^(2/3)) - ((sqrt(c)*d + sqrt(a)*e)*log(a^(1/6) - c^(1/6)*x))/(6*a^(5/6)*c^(2/3)) + ((d - (sqrt(a)*e)/sqrt(c))*log(a^(1/6) + c^(1/6)*x))/(6*a^(5/6)*c^(1/6)) - ((d - (sqrt(a)*e)/sqrt(c))*log(a^(1/3) - a^(1/6)*c^(1/6)*x + c^(1/3)*x^2))/(12*a^(5/6)*c^(1/6)) + ((sqrt(c)*d + sqrt(a)*e)*log(a^(1/3) + a^(1/6)*c^(1/6)*x + c^(1/3)*x^2))/(12*a^(5/6)*c^(2/3))]


    #= ::Title::Closed:: =#
    #=Integrands*of*the*form*(d+e*x^4)^q*(a+b*x^4+c*x^8)^p=#


    #= ::Section::Closed:: =#
    #=Integrands*of*the*form*(d+e*x^4)^q*(a+b*x^4+c*x^8)^p*with*b=0=#


    @test_int [(d + e*x^4)/(a + c*x^8), x, 19, If(13>=8, -((sqrt(2 - sqrt(2))*((1 + sqrt(2))*sqrt(c)*d - sqrt(a)*e)*atan((sqrt(2 - sqrt(2))*a^(1/8) - 2*c^(1/8)*x)/(sqrt(2 + sqrt(2))*a^(1/8))))/(8*a^(7/8)*c^(5/8))) + (sqrt(2 + sqrt(2))*((1 - sqrt(2))*sqrt(c)*d - sqrt(a)*e)*atan((sqrt(2 + sqrt(2))*a^(1/8) - 2*c^(1/8)*x)/(sqrt(2 - sqrt(2))*a^(1/8))))/(8*a^(7/8)*c^(5/8)) + (sqrt(2 - sqrt(2))*((1 + sqrt(2))*sqrt(c)*d - sqrt(a)*e)*atan((sqrt(2 - sqrt(2))*a^(1/8) + 2*c^(1/8)*x)/(sqrt(2 + sqrt(2))*a^(1/8))))/(8*a^(7/8)*c^(5/8)) - (sqrt(2 + sqrt(2))*((1 - sqrt(2))*sqrt(c)*d - sqrt(a)*e)*atan((sqrt(2 + sqrt(2))*a^(1/8) + 2*c^(1/8)*x)/(sqrt(2 - sqrt(2))*a^(1/8))))/(8*a^(7/8)*c^(5/8)) + (((1 - sqrt(2))*sqrt(c)*d - sqrt(a)*e)*log(a^(1/4) - sqrt(2 - sqrt(2))*a^(1/8)*c^(1/8)*x + c^(1/4)*x^2))/(8*sqrt(2*(2 - sqrt(2)))*a^(7/8)*c^(5/8)) - (((1 - sqrt(2))*sqrt(c)*d - sqrt(a)*e)*log(a^(1/4) + sqrt(2 - sqrt(2))*a^(1/8)*c^(1/8)*x + c^(1/4)*x^2))/(8*sqrt(2*(2 - sqrt(2)))*a^(7/8)*c^(5/8)) - (((1 + sqrt(2))*sqrt(c)*d - sqrt(a)*e)*log(a^(1/4) - sqrt(2 + sqrt(2))*a^(1/8)*c^(1/8)*x + c^(1/4)*x^2))/(8*sqrt(2*(2 + sqrt(2)))*a^(7/8)*c^(5/8)) + ((d + sqrt(2)*d - (sqrt(a)*e)/sqrt(c))*log(a^(1/4) + sqrt(2 + sqrt(2))*a^(1/8)*c^(1/8)*x + c^(1/4)*x^2))/(8*sqrt(2*(2 + sqrt(2)))*a^(7/8)*c^(1/8)), -((sqrt(2 - sqrt(2))*((1 + sqrt(2))*sqrt(c)*d - sqrt(a)*e)*atan((sqrt(2 - sqrt(2))*a^(1/8) - 2*c^(1/8)*x)/(sqrt(2 + sqrt(2))*a^(1/8))))/(8*a^(7/8)*c^(5/8))) + (((1 - sqrt(2))*sqrt(c)*d - sqrt(a)*e)*atan((sqrt(2 + sqrt(2))*a^(1/8) - 2*c^(1/8)*x)/(sqrt(2 - sqrt(2))*a^(1/8))))/(4*sqrt(2*(2 - sqrt(2)))*a^(7/8)*c^(5/8)) + (sqrt(2 - sqrt(2))*((1 + sqrt(2))*sqrt(c)*d - sqrt(a)*e)*atan((sqrt(2 - sqrt(2))*a^(1/8) + 2*c^(1/8)*x)/(sqrt(2 + sqrt(2))*a^(1/8))))/(8*a^(7/8)*c^(5/8)) - (((1 - sqrt(2))*sqrt(c)*d - sqrt(a)*e)*atan((sqrt(2 + sqrt(2))*a^(1/8) + 2*c^(1/8)*x)/(sqrt(2 - sqrt(2))*a^(1/8))))/(4*sqrt(2*(2 - sqrt(2)))*a^(7/8)*c^(5/8)) + (((1 - sqrt(2))*sqrt(c)*d - sqrt(a)*e)*log(a^(1/4) - sqrt(2 - sqrt(2))*a^(1/8)*c^(1/8)*x + c^(1/4)*x^2))/(8*sqrt(2*(2 - sqrt(2)))*a^(7/8)*c^(5/8)) - (((1 - sqrt(2))*sqrt(c)*d - sqrt(a)*e)*log(a^(1/4) + sqrt(2 - sqrt(2))*a^(1/8)*c^(1/8)*x + c^(1/4)*x^2))/(8*sqrt(2*(2 - sqrt(2)))*a^(7/8)*c^(5/8)) - (((1 + sqrt(2))*sqrt(c)*d - sqrt(a)*e)*log(a^(1/4) - sqrt(2 + sqrt(2))*a^(1/8)*c^(1/8)*x + c^(1/4)*x^2))/(8*sqrt(2*(2 + sqrt(2)))*a^(7/8)*c^(5/8)) + ((d + sqrt(2)*d - (sqrt(a)*e)/sqrt(c))*log(a^(1/4) + sqrt(2 + sqrt(2))*a^(1/8)*c^(1/8)*x + c^(1/4)*x^2))/(8*sqrt(2*(2 + sqrt(2)))*a^(7/8)*c^(1/8)))]
    @test_int [(d + e*x^4)/(a - c*x^8), x, 13, ((sqrt(c)*d + sqrt(a)*e)*atan((c^(1/8)*x)/a^(1/8)))/(4*a^(7/8)*c^(5/8)) - ((d - (sqrt(a)*e)/sqrt(c))*atan(1 - (sqrt(2)*c^(1/8)*x)/a^(1/8)))/(4*sqrt(2)*a^(7/8)*c^(1/8)) + ((d - (sqrt(a)*e)/sqrt(c))*atan(1 + (sqrt(2)*c^(1/8)*x)/a^(1/8)))/(4*sqrt(2)*a^(7/8)*c^(1/8)) + ((sqrt(c)*d + sqrt(a)*e)*atanh((c^(1/8)*x)/a^(1/8)))/(4*a^(7/8)*c^(5/8)) - ((d - (sqrt(a)*e)/sqrt(c))*log(a^(1/4) - sqrt(2)*a^(1/8)*c^(1/8)*x + c^(1/4)*x^2))/(8*sqrt(2)*a^(7/8)*c^(1/8)) + ((d - (sqrt(a)*e)/sqrt(c))*log(a^(1/4) + sqrt(2)*a^(1/8)*c^(1/8)*x + c^(1/4)*x^2))/(8*sqrt(2)*a^(7/8)*c^(1/8))]


    #= ::Section::Closed:: =#
    #=Integrands*of*the*form*(d+e*x^4)^q*(a+b*x^4+c*x^8)^p*with*c*d^2-a*e^2=0=#


    #= ::Subsection::Closed:: =#
    #=Integrands*of*the*form*(d+e*x^4)^q*(a+b*x^4+c*x^8)^p*with*c*d^2-a*e^2=0=#


    #= ::Subsubsection:: =#
    #=p>0=#


    #= ::Subsubsection::Closed:: =#
    #=p<0=#


    @test_int [(d + e*x^4)/(d^2 + b*x^4 + e^2*x^8), x, 19, -(atan((sqrt(2*sqrt(d)*sqrt(e) - sqrt(-b + 2*d*e)) - 2*sqrt(e)*x)/sqrt(2*sqrt(d)*sqrt(e) + sqrt(-b + 2*d*e)))/(4*sqrt(d)*sqrt(2*sqrt(d)*sqrt(e) + sqrt(-b + 2*d*e)))) - atan((sqrt(2*sqrt(d)*sqrt(e) + sqrt(-b + 2*d*e)) - 2*sqrt(e)*x)/sqrt(2*sqrt(d)*sqrt(e) - sqrt(-b + 2*d*e)))/(4*sqrt(d)*sqrt(2*sqrt(d)*sqrt(e) - sqrt(-b + 2*d*e))) + atan((sqrt(2*sqrt(d)*sqrt(e) - sqrt(-b + 2*d*e)) + 2*sqrt(e)*x)/sqrt(2*sqrt(d)*sqrt(e) + sqrt(-b + 2*d*e)))/(4*sqrt(d)*sqrt(2*sqrt(d)*sqrt(e) + sqrt(-b + 2*d*e))) + atan((sqrt(2*sqrt(d)*sqrt(e) + sqrt(-b + 2*d*e)) + 2*sqrt(e)*x)/sqrt(2*sqrt(d)*sqrt(e) - sqrt(-b + 2*d*e)))/(4*sqrt(d)*sqrt(2*sqrt(d)*sqrt(e) - sqrt(-b + 2*d*e))) - log(sqrt(d) - sqrt(2*sqrt(d)*sqrt(e) - sqrt(-b + 2*d*e))*x + sqrt(e)*x^2)/(8*sqrt(d)*sqrt(2*sqrt(d)*sqrt(e) - sqrt(-b + 2*d*e))) + log(sqrt(d) + sqrt(2*sqrt(d)*sqrt(e) - sqrt(-b + 2*d*e))*x + sqrt(e)*x^2)/(8*sqrt(d)*sqrt(2*sqrt(d)*sqrt(e) - sqrt(-b + 2*d*e))) - log(sqrt(d) - sqrt(2*sqrt(d)*sqrt(e) + sqrt(-b + 2*d*e))*x + sqrt(e)*x^2)/(8*sqrt(d)*sqrt(2*sqrt(d)*sqrt(e) + sqrt(-b + 2*d*e))) + log(sqrt(d) + sqrt(2*sqrt(d)*sqrt(e) + sqrt(-b + 2*d*e))*x + sqrt(e)*x^2)/(8*sqrt(d)*sqrt(2*sqrt(d)*sqrt(e) + sqrt(-b + 2*d*e)))]
    @test_int [(d + e*x^4)/(d^2 + f*x^4 + e^2*x^8), x, 19, -(atan((sqrt(2*sqrt(d)*sqrt(e) - sqrt(2*d*e - f)) - 2*sqrt(e)*x)/sqrt(2*sqrt(d)*sqrt(e) + sqrt(2*d*e - f)))/(4*sqrt(d)*sqrt(2*sqrt(d)*sqrt(e) + sqrt(2*d*e - f)))) - atan((sqrt(2*sqrt(d)*sqrt(e) + sqrt(2*d*e - f)) - 2*sqrt(e)*x)/sqrt(2*sqrt(d)*sqrt(e) - sqrt(2*d*e - f)))/(4*sqrt(d)*sqrt(2*sqrt(d)*sqrt(e) - sqrt(2*d*e - f))) + atan((sqrt(2*sqrt(d)*sqrt(e) - sqrt(2*d*e - f)) + 2*sqrt(e)*x)/sqrt(2*sqrt(d)*sqrt(e) + sqrt(2*d*e - f)))/(4*sqrt(d)*sqrt(2*sqrt(d)*sqrt(e) + sqrt(2*d*e - f))) + atan((sqrt(2*sqrt(d)*sqrt(e) + sqrt(2*d*e - f)) + 2*sqrt(e)*x)/sqrt(2*sqrt(d)*sqrt(e) - sqrt(2*d*e - f)))/(4*sqrt(d)*sqrt(2*sqrt(d)*sqrt(e) - sqrt(2*d*e - f))) - log(sqrt(d) - sqrt(2*sqrt(d)*sqrt(e) - sqrt(2*d*e - f))*x + sqrt(e)*x^2)/(8*sqrt(d)*sqrt(2*sqrt(d)*sqrt(e) - sqrt(2*d*e - f))) + log(sqrt(d) + sqrt(2*sqrt(d)*sqrt(e) - sqrt(2*d*e - f))*x + sqrt(e)*x^2)/(8*sqrt(d)*sqrt(2*sqrt(d)*sqrt(e) - sqrt(2*d*e - f))) - log(sqrt(d) - sqrt(2*sqrt(d)*sqrt(e) + sqrt(2*d*e - f))*x + sqrt(e)*x^2)/(8*sqrt(d)*sqrt(2*sqrt(d)*sqrt(e) + sqrt(2*d*e - f))) + log(sqrt(d) + sqrt(2*sqrt(d)*sqrt(e) + sqrt(2*d*e - f))*x + sqrt(e)*x^2)/(8*sqrt(d)*sqrt(2*sqrt(d)*sqrt(e) + sqrt(2*d*e - f)))]

    @test_int [(d + e*x^4)/(d^2 - b*x^4 + e^2*x^8), x, 7, -((sqrt(e)*atan((sqrt(2)*sqrt(e)*x)/sqrt(sqrt(b - 2*d*e) - sqrt(b + 2*d*e))))/(sqrt(2)*sqrt(b - 2*d*e)*sqrt(sqrt(b - 2*d*e) - sqrt(b + 2*d*e)))) - (sqrt(e)*atan((sqrt(2)*sqrt(e)*x)/sqrt(sqrt(b - 2*d*e) + sqrt(b + 2*d*e))))/(sqrt(2)*sqrt(b - 2*d*e)*sqrt(sqrt(b - 2*d*e) + sqrt(b + 2*d*e))) - (sqrt(e)*atanh((sqrt(2)*sqrt(e)*x)/sqrt(sqrt(b - 2*d*e) - sqrt(b + 2*d*e))))/(sqrt(2)*sqrt(b - 2*d*e)*sqrt(sqrt(b - 2*d*e) - sqrt(b + 2*d*e))) - (sqrt(e)*atanh((sqrt(2)*sqrt(e)*x)/sqrt(sqrt(b - 2*d*e) + sqrt(b + 2*d*e))))/(sqrt(2)*sqrt(b - 2*d*e)*sqrt(sqrt(b - 2*d*e) + sqrt(b + 2*d*e)))]
    @test_int [(d + e*x^4)/(d^2 - f*x^4 + e^2*x^8), x, 19, -(atan((sqrt(2*sqrt(d)*sqrt(e) - sqrt(2*d*e + f)) - 2*sqrt(e)*x)/sqrt(2*sqrt(d)*sqrt(e) + sqrt(2*d*e + f)))/(4*sqrt(d)*sqrt(2*sqrt(d)*sqrt(e) + sqrt(2*d*e + f)))) - atan((sqrt(2*sqrt(d)*sqrt(e) + sqrt(2*d*e + f)) - 2*sqrt(e)*x)/sqrt(2*sqrt(d)*sqrt(e) - sqrt(2*d*e + f)))/(4*sqrt(d)*sqrt(2*sqrt(d)*sqrt(e) - sqrt(2*d*e + f))) + atan((sqrt(2*sqrt(d)*sqrt(e) - sqrt(2*d*e + f)) + 2*sqrt(e)*x)/sqrt(2*sqrt(d)*sqrt(e) + sqrt(2*d*e + f)))/(4*sqrt(d)*sqrt(2*sqrt(d)*sqrt(e) + sqrt(2*d*e + f))) + atan((sqrt(2*sqrt(d)*sqrt(e) + sqrt(2*d*e + f)) + 2*sqrt(e)*x)/sqrt(2*sqrt(d)*sqrt(e) - sqrt(2*d*e + f)))/(4*sqrt(d)*sqrt(2*sqrt(d)*sqrt(e) - sqrt(2*d*e + f))) - log(sqrt(d) - sqrt(2*sqrt(d)*sqrt(e) - sqrt(2*d*e + f))*x + sqrt(e)*x^2)/(8*sqrt(d)*sqrt(2*sqrt(d)*sqrt(e) - sqrt(2*d*e + f))) + log(sqrt(d) + sqrt(2*sqrt(d)*sqrt(e) - sqrt(2*d*e + f))*x + sqrt(e)*x^2)/(8*sqrt(d)*sqrt(2*sqrt(d)*sqrt(e) - sqrt(2*d*e + f))) - log(sqrt(d) - sqrt(2*sqrt(d)*sqrt(e) + sqrt(2*d*e + f))*x + sqrt(e)*x^2)/(8*sqrt(d)*sqrt(2*sqrt(d)*sqrt(e) + sqrt(2*d*e + f))) + log(sqrt(d) + sqrt(2*sqrt(d)*sqrt(e) + sqrt(2*d*e + f))*x + sqrt(e)*x^2)/(8*sqrt(d)*sqrt(2*sqrt(d)*sqrt(e) + sqrt(2*d*e + f)))]


    @test_int [(1 + x^4)/(1 + b*x^4 + x^8), x, 19, -(atan((sqrt(2 - sqrt(2 - b)) - 2*x)/sqrt(2 + sqrt(2 - b)))/(4*sqrt(2 + sqrt(2 - b)))) - atan((sqrt(2 + sqrt(2 - b)) - 2*x)/sqrt(2 - sqrt(2 - b)))/(4*sqrt(2 - sqrt(2 - b))) + atan((sqrt(2 - sqrt(2 - b)) + 2*x)/sqrt(2 + sqrt(2 - b)))/(4*sqrt(2 + sqrt(2 - b))) + atan((sqrt(2 + sqrt(2 - b)) + 2*x)/sqrt(2 - sqrt(2 - b)))/(4*sqrt(2 - sqrt(2 - b))) - log(1 - sqrt(2 - sqrt(2 - b))*x + x^2)/(8*sqrt(2 - sqrt(2 - b))) + log(1 + sqrt(2 - sqrt(2 - b))*x + x^2)/(8*sqrt(2 - sqrt(2 - b))) - log(1 - sqrt(2 + sqrt(2 - b))*x + x^2)/(8*sqrt(2 + sqrt(2 - b))) + log(1 + sqrt(2 + sqrt(2 - b))*x + x^2)/(8*sqrt(2 + sqrt(2 - b)))]

    @test_int [(1 + x^4)/(1 + 3*x^4 + x^8), x, 19, -(((3 + sqrt(5))^(1/4)*atan(1 - (2^(3/4)*x)/(3 - sqrt(5))^(1/4)))/(2*2^(3/4)*sqrt(5))) + ((3 + sqrt(5))^(1/4)*atan(1 + (2^(3/4)*x)/(3 - sqrt(5))^(1/4)))/(2*2^(3/4)*sqrt(5)) - ((3 - sqrt(5))^(1/4)*atan(1 - (2^(3/4)*x)/(3 + sqrt(5))^(1/4)))/(2*2^(3/4)*sqrt(5)) + ((3 - sqrt(5))^(1/4)*atan(1 + (2^(3/4)*x)/(3 + sqrt(5))^(1/4)))/(2*2^(3/4)*sqrt(5)) - ((3 + sqrt(5))^(1/4)*log(sqrt(2*(3 - sqrt(5))) - 2*(2*(3 - sqrt(5)))^(1/4)*x + 2*x^2))/(4*2^(3/4)*sqrt(5)) + ((3 + sqrt(5))^(1/4)*log(sqrt(2*(3 - sqrt(5))) + 2*(2*(3 - sqrt(5)))^(1/4)*x + 2*x^2))/(4*2^(3/4)*sqrt(5)) - ((3 - sqrt(5))^(1/4)*log(sqrt(2*(3 + sqrt(5))) - 2*(2*(3 + sqrt(5)))^(1/4)*x + 2*x^2))/(4*2^(3/4)*sqrt(5)) + ((3 - sqrt(5))^(1/4)*log(sqrt(2*(3 + sqrt(5))) + 2*(2*(3 + sqrt(5)))^(1/4)*x + 2*x^2))/(4*2^(3/4)*sqrt(5))]
    @test_int [(1 + x^4)/(1 + 2*x^4 + x^8), x, 10, -(atan(1 - sqrt(2)*x)/(2*sqrt(2))) + atan(1 + sqrt(2)*x)/(2*sqrt(2)) - log(1 - sqrt(2)*x + x^2)/(4*sqrt(2)) + log(1 + sqrt(2)*x + x^2)/(4*sqrt(2))]
    @test_int [(1 + x^4)/(1 + 1*x^4 + x^8), x, 19, -(atan((1 - 2*x)/sqrt(3))/(4*sqrt(3))) - (1/4)*atan(sqrt(3) - 2*x) + atan((1 + 2*x)/sqrt(3))/(4*sqrt(3)) + (1/4)*atan(sqrt(3) + 2*x) - (1/8)*log(1 - x + x^2) + (1/8)*log(1 + x + x^2) - log(1 - sqrt(3)*x + x^2)/(8*sqrt(3)) + log(1 + sqrt(3)*x + x^2)/(8*sqrt(3))]
    @test_int [(1 + x^4)/(1 + 0*x^4 + x^8), x, 19, (-(1/4))*sqrt((1/2)*(2 - sqrt(2)))*atan((sqrt(2 - sqrt(2)) - 2*x)/sqrt(2 + sqrt(2))) - (1/4)*sqrt((1/2)*(2 + sqrt(2)))*atan((sqrt(2 + sqrt(2)) - 2*x)/sqrt(2 - sqrt(2))) + (1/4)*sqrt((1/2)*(2 - sqrt(2)))*atan((sqrt(2 - sqrt(2)) + 2*x)/sqrt(2 + sqrt(2))) + (1/4)*sqrt((1/2)*(2 + sqrt(2)))*atan((sqrt(2 + sqrt(2)) + 2*x)/sqrt(2 - sqrt(2))) - log(1 - sqrt(2 - sqrt(2))*x + x^2)/(8*sqrt(2 - sqrt(2))) + log(1 + sqrt(2 - sqrt(2))*x + x^2)/(8*sqrt(2 - sqrt(2))) - log(1 - sqrt(2 + sqrt(2))*x + x^2)/(8*sqrt(2 + sqrt(2))) + log(1 + sqrt(2 + sqrt(2))*x + x^2)/(8*sqrt(2 + sqrt(2)))]
    @test_int [(1 + x^4)/(1 - 1*x^4 + x^8), x, 19, (-(1/4))*sqrt(2 - sqrt(3))*atan((sqrt(2 - sqrt(3)) - 2*x)/sqrt(2 + sqrt(3))) - (1/4)*sqrt(2 + sqrt(3))*atan((sqrt(2 + sqrt(3)) - 2*x)/sqrt(2 - sqrt(3))) + (1/4)*sqrt(2 - sqrt(3))*atan((sqrt(2 - sqrt(3)) + 2*x)/sqrt(2 + sqrt(3))) + (1/4)*sqrt(2 + sqrt(3))*atan((sqrt(2 + sqrt(3)) + 2*x)/sqrt(2 - sqrt(3))) - log(1 - sqrt(2 - sqrt(3))*x + x^2)/(8*sqrt(2 - sqrt(3))) + log(1 + sqrt(2 - sqrt(3))*x + x^2)/(8*sqrt(2 - sqrt(3))) - log(1 - sqrt(2 + sqrt(3))*x + x^2)/(8*sqrt(2 + sqrt(3))) + log(1 + sqrt(2 + sqrt(3))*x + x^2)/(8*sqrt(2 + sqrt(3)))]
    @test_int [(1 + x^4)/(1 - 2*x^4 + x^8), x, 5, x/(2*(1 - x^4)) + atan(x)/4 + atanh(x)/4]
    @test_int [(1 + x^4)/(1 - 3*x^4 + x^8), x, 7, atan(sqrt(2/(-1 + sqrt(5)))*x)/sqrt(2*(-1 + sqrt(5))) - atan(sqrt(2/(1 + sqrt(5)))*x)/sqrt(2*(1 + sqrt(5))) + atanh(sqrt(2/(-1 + sqrt(5)))*x)/sqrt(2*(-1 + sqrt(5))) - atanh(sqrt(2/(1 + sqrt(5)))*x)/sqrt(2*(1 + sqrt(5)))]
    @test_int [(1 + x^4)/(1 - 4*x^4 + x^8), x, 7, atan((2^(1/4)*x)/sqrt(-1 + sqrt(3)))/(2*2^(1/4)*sqrt(-1 + sqrt(3))) - atan((2^(1/4)*x)/sqrt(1 + sqrt(3)))/(2*2^(1/4)*sqrt(1 + sqrt(3))) + atanh((2^(1/4)*x)/sqrt(-1 + sqrt(3)))/(2*2^(1/4)*sqrt(-1 + sqrt(3))) - atanh((2^(1/4)*x)/sqrt(1 + sqrt(3)))/(2*2^(1/4)*sqrt(1 + sqrt(3)))]
    @test_int [(1 + x^4)/(1 - 5*x^4 + x^8), x, 7, atan(sqrt(2/(-sqrt(3) + sqrt(7)))*x)/sqrt(6*(-sqrt(3) + sqrt(7))) - atan(sqrt(2/(sqrt(3) + sqrt(7)))*x)/sqrt(6*(sqrt(3) + sqrt(7))) + atanh(sqrt(2/(-sqrt(3) + sqrt(7)))*x)/sqrt(6*(-sqrt(3) + sqrt(7))) - atanh(sqrt(2/(sqrt(3) + sqrt(7)))*x)/sqrt(6*(sqrt(3) + sqrt(7)))]
